options.query:
select * from interactions i INNER JOIN contacts c
    ON c.id = i."contactId" JOIN accounts a
    ON c."accountId" = a."id"  join  interaction_types it on it.id = i."interactionTypeId" where c."owner" ='{{globals.currentUser.email}}' and c."id"= '{{variables.contactId}}';

select * from campaigns cp INNER JOIN contacts c
    ON c.id = cp."contactId"  JOIN accounts a
    ON c."accountId" = a."id"  join  campaign_types ct on ct.id = cp."id" where c."owner" ='{{globals.currentUser.email}}' and c."id"= '{{variables.contactId}}';

