const cols = Object.values(components.contactInteractionTable.changeSet).map((col, index) => {
  return {
    col: Object.keys(col),
    id: components.contactInteractionTable.dataUpdates[index].id,
    values: Object.values(col),
  };
});

const sql = cols.map((column) => {
  const { col, id, values } = column;
  const cols = col.map((col, index) => `"${col}" = '${values[index]}'`);
  return `UPDATE interactions SET ${cols.join(", ")} WHERE id = '${id}';`;
});

return sql

